package com.cultureambassadors.albanointour;

import java.util.ArrayList;
import java.util.HashMap;

public class BCList
{
    private static final ArrayList<BC> allBc = new ArrayList<>();
    private static BCList bcList;
    
    private BCList()
    {
        allBc.add(new BC("Duomo di Albano", "È sorto sulle rovine della basilica costruita dall’Imperatore Costantino; nella cripta si possono ancora trovare alcuni dei capitelli di stile ionico della precedente basilica paleocristiana, fatta riedificare da papa Leone III (795-815) dopo un devastante incendio. Successivamente subì varie modifiche, quali la costruzione di un cimitero collegato alla cattedrale e l’edificazione di una piccola sacrestia, ma la modifica maggiore si ebbe nel 1772, quando il Cardinal Fabrizio Paolucci ordinò la costruzione della facciata attuale, ad opera dell’architetto Carlo Buratti. L’aspetto attuale della basilica invece fu ottenuto solamente nel 1913, dopo diversi restauri ed adeguamenti (durati circa cinquant’anni) realizzati con il contributo economico della popolazione Albanense. Grazie a questi restauri fu possibile porre in rilievo una serie di colonne appartenenti alla cattedrale medioevale di Leone III; successivamente la chiesa fu dedicata oltre che a S. Giovanni Battista, anche a S. Pancrazio, ambedue santi protettori di Albano.  All’interno del complesso è custodito inoltre un magnifico sarcofago di marmo di epoca paleocristiana, figurante un orante centrale. L’interno della cattedrale è in pieno gusto neoclassico, suddiviso in tre navate con sei cappelle laterali, dove trovano posto degli splendidi dipinti del XVIII secolo. Nell’abside, al di sopra di tre quadri raffiguranti rispettivamente: il ritrovamento della Croce da parte di sant’Elena (sinistra), S. Pancrazio (centro) e l’apparizione della Santa Croce a Costantino (destra), trova posto il monogramma di Cristo. A pochi metri di distanza fu edificato nel 1752 il palazzo episcopale, voluto dal Cardinale Nicola Lercari, all’epoca Segretario di Stato di Papa Benedetto XIII.", "Piazza Duomo", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "30 minuti", "Gratuito", "Religiosi", "duomo"));
        allBc.add(new BC("Convento dei Frati Cappuccini e Chiesa di S. Francesco", "La costruzione fu eretta su pianta conventuale nel 1619. La chiesa presenta un’unica navata e manifesta completamente la povertà dello spirito Francescano. Sopra l’altare è presente una splendida pala di dimensioni imponenti, realizzata nel 1618 dall’artista Gherardo delle Notti. Il dipinto raffigura la principessa Flaminia Colonna Gonzaga che si rivolge verso la Madonna con il Bambino, nel frattempo S. Bonaventura è incantato nella visione di S. Francesco; alle spalle di questa scena si può intravedere il convento di Palazzola con il retrostante Monte Cavo. Nella cappella di destra si trova un gruppo scultoreo rappresentante la natività: Gesù Bambino è circondato dalla Madonna, S. Giuseppe, il bue e l’asinello. L’opera fu realizzata da A. Bolgi e da S. Speranza (ambedue provenienti dalla scuola del Bernini) nel 1633.", "Via S. Francesco d'Assisi", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "1 ora", "Gratuito", "Religiosi", "cappuccini"));
        allBc.add(new BC("Convento e Chiesa di S. Paolo", "Furono eretti intorno all’anno 1282 dal Cardinale Giacomo Savelli, che successivamente divenne papa con il nome di Onorio IV.Nel 1710 il pittore Gaspar Van Wittel, in onore della visita ad Albano del Papa Clemente XI, ritrasse la chiesa e l’annesso convento. (Oggi al Museo Pitti di Firenze). L’intero complesso fu fatto restaurare da Marco Antonio Colonna nel 1769, subendo modifiche rilevanti soprattutto negli interni che furono rimaneggiati sino ad assumere uno stile baroccheggiante; di contro la facciata si presenta con uno stile neoclassico dell’ottocento. Numerose sono le tele che abbelliscono la chiesa, tutte realizzate fra il seicento ed il settecento, particolarmente degna di nota è quella situata alle spalle dell’altare, dove viene raffigurato S. Paolo ricevente l’unzione; il quadro fu realizzato nel tardo seicento da Pietro da Cortona. La volta della navata è ornata con uno splendido affresco dell’ottocento, che viene attribuito al Gagliardi, artista molto attivo in quel tempo ad Albano. Nella cappella laterale di sinistra, proprio sotto l’altare è conservato il corpo di S. Gaspare Del Bufalo, famoso fondatore dei PP. Missionari del Preziosissimo Sangue.", "Piazza S. Paolo", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "30 minuti", "Gratuito", "Religiosi", "san_paolo"));
        allBc.add(new BC("Chiesa della Stella e Cimitero del Colera", "Il convento e la chiesa vennero edificati verso la metà del XVI sec., probabilmente dove si trovava precedentemente la chiesa di S. Senatore, sopra le omonime catacombe. Nel 1687, grazie all’importantissimo contributo del Comune di Albano il complesso raggiunse lo stato attuale. L’altare maggiore è realizzato in stile barocco, come tutte le altre decorazioni della chiesa, ed è ornato da marmi policromi posti nel settecento. Alle spalle di esso si presenta una struttura con timpano, che al centro ospita il quadro della Madonna con Bambino contornato da angeli. Il nome della chiesa viene proprio da un particolare di questo quadro, infatti sul manto della Vergine Maria è presente una stella. Quattro statue di gesso sono poste sul muro che divide a metà il presbiterio. Sulla parete di fondo inoltre fa la sua spettacolare figura lo stemma dei Principi Savelli, che furono signori di Albano per molti secoli. Vicino la chiesa si trova il cimitero storico comunale, sulla cui entrata originaria si conserva ancora l’iscrizione dell’anno di consacrazione, ovvero 1833.", "Via della Stella, 5", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "30 minuti", "Gratuito", "Religiosi", "stella"));
        allBc.add(new BC("Chiesa di S. Maria della Rotonda", "Il complesso di S. Maria della Rotonda si trova su di un grazioso ninfeo che mostra uno splendido precedente del più famoso Panteon romano. Fu consacrato nel 1060,anche se si iniziò ad usarlo per le celebrazioni ecclesiastiche già nel IX secolo. L’edificio fu più volte trasformato, prima nel seicento e nell’ottocento e successivamente con un ultimo restauro nel 1938 che riportò l’edificio allo splendore dell’età romana. L’interno è indubbiamente di grande fascino e bellezza, fra le opere di maggior imponenza si può annoverare l’antica icona della Madonna con il Bambino, posta sull’altare centrale, sulla destra dell’altare, all’interno dei catini, vi sono dei suggestivi resti di affreschi, fra i quali il meglio conservato è quello della “storia della vera croce” probabilmente realizzato nel XIV sec. Un altro magnifico affresco si può vedere sull’altare destro, realizzato nel XIII sec. È attribuito al Cavallini e raffigura S. Anna, S. Ambrogio e S. Giovanni.Tutti gli altari della chiesa sono stati ricavati da antichi frammenti architettonici di età severiana, dello stesso periodo storico sono la gran parte dei reperti archeologici situati nella sagrestia e nel vano del campanile.", "Piazza della Rotonda", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "30 minuti", "Gratuito", "Religiosi", "rotonda"));
        allBc.add(new BC("Chiesa di S. Pietro", "Voluta da Papa Ormisda (514-523 d.C.) è stata ricavata da una grande sala delle terme romane edificate dall’Imperatore Caracalla. Un imponente restauro avvenne già nel XII sec., mentre nel XIV sec. vennero eseguite diverse modifiche, delle quali ancora oggi si può scorgere la presenza, come l’arco ogivale a due spioventi con mensole e colonne marmoree, situato sopra ad una porta posta sul lato occidentale della chiesa. La chiesa passò successivamente sotto il possesso della famiglia Savelli nel 1440; questi seppellirono, nell’ormai distrutta antica cappella, alcuni dei loro componenti. Queste tombe sono oggi visibili all’interno della chiesa. Anche alcune trabeazioni marmoree dell’età severiana sono conservate all’interno della chiesa, anche se si presentano sotto forma di altari o balaustre. Molti affreschi sono tutt’ora visibili sulle pareti del complesso, fra questi spiccano certamente quello raffigurante La Vergine del Segno, di età bizantina, e quello di dimensioni maggiori raffigurante S. Margherita e S. Onofrio (XIII-XIV sec. d.C.). Di grande impatto sono anche la pala dell’altare (XVI sec.) raffigurante la consegna delle chiavi a S. Pietro e gli stendardi del settecento. Per finire, sul lato esterno della chiesa volto ad oriente si possono ammirare due trabeazioni decorate minuziosamente risalenti all’età imperiale, che sono utilizzate come stipiti della porta; di grande fascino è anche il campanile romanico risalente al XIII sec.", "Piazza S. Pietro", "lunedì:\t8:00-20:00\nmartedì:\t8:00-20:00\nmercoledì:\t8:00-20:00\ngiovedì:\t8:00-20:00\nvenerdì:\t8:00-20:00\nsabato:\t8:00-20:00\ndomenica:\t8:00-20:00", "30 minuti", "Gratuito", "Religiosi", "san_pietro"));
        allBc.add(new BC("Tomba degli Orazi e Curiazi", "Il monumento è un caso unico per la sua storia intrisa nel mito e nella leggenda e per la sua architettura, che si può facilmente ritrovare nelle tombe etrusche a Volterra. Il monumento è totalmente realizzato in grandi parallelepipedi di peperino; sul basamento quadrangolare vi erano quattro tronchi di cono sugli angoli e probabilmente un quinto più imponente sulla base centrale a tamburo. Edificato in età repubblicana il mausoleo non può essere riferito ai fratelli Orazi e Curiazi, alcuni studiosi pensano che il mausoleo sia una ricostruzione della tomba di Arunte, realizzata dalla famiglia Arruntia che presenziava vicino alla zona.", "Via della Stella", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "30 minuti", "Gratuito", "Storici", "orazi_curiazi"));
        allBc.add(new BC("Catacombe di S. Senatore", "Situate sulla Via Appia lungo il XV miglio di distanza da Roma, in precedenza il luogo era una cava di pozzolana. La formazione del cimitero nell’antica cava avvenne tra la fine del III e l’inizio del IV sec. d.C. La fama delle catacombe era dovuta principalmente alla presenza di numerosi santi e martiri, come ricorda il martirologio geronimiano. Nella cripta sono presenti diversi affreschi, fra cui quello raffigurante il Cristo tra gli Sponsores e i martiri Albani (V sec. d.C. inizi VI sec.), quello raffigurante S. Senatore il titolare delle Catacombe (IV-V sec. d.C), un affresco di età medioevale rappresentante Cristo Pantocrator tra S. Smaragdo e la Madonna (XI-XII sec. d.C.); infine vi è un altro affresco situato sull’abside della cripta minore. Tutti i reperti archeologici trovati nella cripta sono esposti nel Museo Civico Albano.", "Via della Stella, 5", "lunedì:\tChiuso\nmartedì:\t09–13\nmercoledì:\t09–13, 15–19\ngiovedì:\tChiuso\nvenerdì:\tChiuso\nsabato:\t15–19\ndomenica:\tChiuso", "1 ora", "ND", "Storici", "catacombe"));
        allBc.add(new BC("I Cisternoni", "La cisterna fu fatta erigere da parte degli architetti della II Legione Partica per garantire il rifornimento idrico necessario all’accampamento e a tutte le abitazioni circostanti; la pianta è semi-rettangolare con lati maggiori misuranti rispettivamente 47,90 m. e 45,50 m. e con lati minori misuranti 29,62 m. e 31,90 m. Fu costruita in parte scavando direttamente il banco roccioso ed in parte in muratura; è divisa in cinque navate, caratterizzate da volte a botte e sostenute da trentasei pilastri ed è rivestita da un opus signimun, ovvero un intonaco impermeabile. Le dimensioni dei Cisternoni sono imponenti e permettono di immagazzinare circa 10.000 metri cubi d’acqua, ma la loro importanza non è data solo da questo, bensì perché sono tutt’oggi perfettamente funzionanti, venendo alimentati da condotte di epoca romana che ricevono acqua dalle sorgenti poste lungo i fianchi del Lago di Albano.", "Via Aurelio Saffi", "lunedì:\t09–12:30, 15:30–18:30\nmartedì:\t09–12:30\nmercoledì:\t09–12:30\ngiovedì:\t09–12:30, 15:30–18:30\nvenerdì:\t09–12:30\nsabato:\t09–12:30, 16–19\ndomenica:\t09–12:30", "1 ora e 30 minuti", "ND", "Storici", "cisternoni"));
        allBc.add(new BC("Accampamento della Seconda Legione Partica (Castra Albana)", "Fatto erigere dall’imperatore Settimio Severo intorno al 202 d.C. per farvi presidiare i circa 6000 uomini della II Legione Partica. I Castra erano forniti di un imponente muro di cinta quadrato, merlato sulla sommità, la pianta di base rettangolare misurava circa 435 m. di lunghezza per 232 m, coprendo così una superficie di 10 ettari, le opere di difesa erano infine ultimate da una serie di torri circolari per gli angoli e quadrangolari lungo i lati. Ad oggi sono visibili ancora dei tratti di mura, diverse torri e grandi resti di edifici, le più importanti porte d’accesso, alcuni segmenti di strada basolata e di opere urbanistiche ed anche rovine di edifici civili, quali magazzini ed abitazioni. I vari reperti rinvenuti durante gli scavi sono conservati nel Museo Civico di Albano.", "Tutta la città", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "ND", "Gratuito", "Storici", "accampamento"));
        allBc.add(new BC("Anfiteatro Severiano", "Edificato dalle maestranze della Legione Albana nei primi anni del III sec. d.C., fu posto oltre il lato Nord Est del Castra. Di forma ellittica fu anch’esso realizzato sia scavando nel banco roccioso, sia operando costruzioni in muratura tramite l’uso di diverse tecniche murarie. Nel Medioevo fu trasformato in una cava di minerali ed in un cimitero cristiano. In questo periodo vennero edificati due oratori, uno ricavato nel III fornice ed un altro scavato nella roccia del lato sinistro della cavea. Oggi dell’edificio originario rimangono solamente il primo piano sostenuto da circa trenta fornici, una parte degli ingressi trionfali e tutta la cavea che vanta un’asse maggiore di 113 m, si calcola che in origine l’anfiteatro fosse alto 22 m.", "Via Anfiteatro Romano", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "1 ora", "Gratuito", "Storici", "anfiteatro"));
        allBc.add(new BC("Terme di Cellomaio", "L’enorme edificio fu fatto realizzare dall’imperatore Caracalla per riallacciare i rapporti con i legionari di Albano in rivolta dopo l’uccisione del fratello dell’imperatore Geta. Il complesso costituito da tre piani, è realizzato in opera cementizia ed è rivestito di una cortina laterizia rossastra, basato su di una pianta quadrangolare presenta delle torri negli angoli. I due piani superiori erano articolati in grandi e vaste aule, con pavimenti marmorei o rivestiti di mosaici ed erano provvisti di enormi finestroni sormontati da arcate, il piano inferiore invece era organizzato come ambiente di servizio ed aveva una funzione di sostruzione. L’edificio fu trasformato in una roccaforte durante l’età medioevale e successivamente occupato da abitazioni civili. Ad oggi si è conservato quasi nella sua interezza.", "Via Volontari del Sangue, 6-8", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "15 minuti", "Gratuito", "Storici", "terme"));
        allBc.add(new BC("Villa di Pompeo Magno", "Villa costruita da Pompeo tra il 61 e il 58 a.C., grazie alle ricchezze accumulate con la guerra mitridatica. Subì numerosi cambi di padronanza passando prima da Pompeo al figlio Sesto, poi nelle mani di Dolabella e successivamente nei possedimenti dell’imperatore Augusto e dei suoi successori. I resti a noi arrivati occupano l’imponente dimensione di 9 ettari di superficie, con una larghezza di 260 mt. ed una lunghezza di 340 mt. Osservando le strutture murarie si può notare che la villa fu ristrutturata, cambiata ed ampliata ben quattro volte. Il corpo centrale era rivolto verso il mare e si protraeva su una platea artificiale, raggiungendo i tre piani d’altezza.  Ad abbellire ulteriormente la villa vi erano una serie di ninfei, costruzioni isolate e criptoportici, senza considerare la presenza di numerose e magnifiche statue, fontane, decorazioni e giardini. Alcuni fra i reperti più famosi rinvenuti tra il 1700 ed il 1800, sono l’ara marmorea con scolpite le fatiche di Ercole, il Bacco Barbato e il gruppo di due centauri in marmi policromi. Tutto ciò è oggi conservato nel museo dei Doria Pamphili, antichi possessori del terreno sul quale sorgono i resti della villa. Altri reperti si trovano nel Museo Civico di Albano.", "Piazza Giuseppe Mazzini (all'interno di Villa Doria)", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "15 minuti", "Gratuito", "Storici", "villa_pompeo"));
        allBc.add(new BC("Villa romana ai Cavallacci", "Sorta alla fine dell’età repubblicana, la villa visse il suo periodo di massimo splendore in età Tiberiana, continuando ad essere abitata sino al V sec. d.C..La villa ha finora donato all’archeologia numerosi pezzi di grande valore, fra cui molte strutture murarie, pavimenti in mosaico e marmi policromi. Inoltre fra i numerosi reperti trovati spiccano le terrecotte e la testa di Tiberio Gemello.Tutti questi reperti sono visitabili nel Museo Civico Albano.", "Via Giuseppe Verdi, 140", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "30 minuti", "Gratuito", "Storici", "villa_romana"));
        allBc.add(new BC("Porta Pretoria e Porta Principalis Sinistra", "La Porta Pretoria fu realizzata in base quadrata con parallelepipedi in peperino larga 36m ed alta 14m, è formata da tre fornici coperti ai lati da due avancorpi costituiti da torri di pianta rettangolare, ed è suddivisa su due piani. Il frontone viene mostrato alla sottostante Via Appia ed è ornato da elementi architettonici e statue di marmo, delle quali oggi si hanno solo alcuni resti. La Porta Principale Sinistra è anch’essa formata da tre fornici ed una posterula. Costruita rigorosamente su base quadrata ad oggi si sono conservati il fornice centrale, di dimensioni maggiori rispetto a quelli laterali ed il fornice destro con la vicina posterula, entrambi furono tamponati in età medioevale.", "Via Alcide De Gasperi", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "15 minuti", "Gratuito", "Storici", "porte"));
        allBc.add(new BC("Museo archeologico di Albano", "All’interno di uno splendido parco, con sede nell’edificio neoclassico di Villa Ferraioli, si trova il Museo Archeologico di Albano. Presentandosi con una facciata dotata di un avancorpo centrale a forma di pronao tetrastilo ed ornata da un frontone illustrante il mito di Cerere che insegna l’agricoltura a Trittolemo, il museo ci introduce così alle splendide decorazioni interne, realizzate con stile classico-rinascimentale da Giovan Battista Caretti. Le 23 stanze sono articolate su tre piani, esponendo all’interno una serie di reperti archeologici risalenti a periodi storici differenti, che vanno da un più recente medioevo, sino all’antichissimo paleolitico. Importantissimi i reperti della fauna paleolitica, risalenti all’età della pietra (300.000 - 30.000 anni fa). Inoltre spiccano nel repertorio del museo i vari manufatti dell’età del bronzo e quelli che concernono il periodo di maggior splendore della Civiltà Laziale(Periodo “Albano” XI - IX sec. a.C.). L’età arcaica viene indicata dalla presenza di una testa policroma V sec. a.C. rappresentante un guerriero latino e da un altorilievo templare raffigurante una danza di menadi e satiri. Quanto a presenze archeologiche, invece, il periodo repubblicano-imperiale viene rappresentato da una serie di oggetti votivi provenienti dai santuari arcaico-repubblicani della zona, e da una serie di oggetti appartenenti alla vita quotidiana, suddivisi fra loro in base alle differenti tematiche. Successivamente le sale vanno a documentare la presenza di numerose ville imperiali nella zona quali la villa di Cavallacci, nella quale è stata rinvenuta la testa marmorea di Tiberio Gemello, oppure la villa di Pompeo Magno, che ha donato a noi parecchi reperti marmorei di gran valore, e per finire la villa presso Cancelliera, luogo di provenienza della testa della statua di Faustina Maggiore. Per completare la visita non resta che godersi i vari punti video ed il ricco sistema didattico presente nel museo; va ammirata inoltre la collezione di grafica antica, con autori che spaziano dal ’600 al ’900, con un numero molto alto di opere di Giovan Battista Piranesi.", "Viale Risorgimento n.3", "lunedì:\t09:00-13:00; 15:30-18:30\nmartedì:\t09:00-13:00\nmercoledì:\t09:00-13:00\ngiovedì:\t09:00-13:00; 15:30-18:30\nvenerdì:\tchiuso\nsabato:\t09:00-13:00;\ndomenica:\t09:00 -13:00", "1 ora e 30 minuti", "Singoli: 3 euro\nGruppi (max 30): 40 euro", "Musei", "museo_archeologico"));
        allBc.add(new BC("Museo della Seconda Legione Partica", "Il museo è dedicato alla Seconda Legione Partica la quale, come la prima e la terza, fu creata dall’imperatore Settimio Severo tra gli anni 196 e 197 dopo Cristo per combattere l’assai pugnace popolo dei Parti. Nell’anno 202 d.c. la Seconda Legione fu posta in Albano, vicino Roma, dove fu stabilito il suo quartier generale. La scelta di questo sito rappresentava un’eccezione alla regola generale secondo cui le legioni stazionavano ai confini dell’impero e ciò è dovuto alle particolari doti belliche della legione stessa che fu eletta Guardia Personale dell’imperatore e assunse anche un ruolo di rinforzo per le guarnigioni intorno a Roma. I locali del museo insistono nell’area assai suggestiva dove Caracalla, figlio e successore di Settimio Severo, fece costruire le ampie terme per i legionari. Il museo si apre con una serie di fedeli ricostruzioni di equipaggiamenti e vestiari delle più eminenti figure della Legione, risultato di ricerche effettuate nell’ambito dell’archeologia sperimentale. Tra queste possiamo annoverare il Prefetto della legione “Praefectus Legionis” di rango Equestre che sostituiva il legato, di rango senatoriale, in tutte e tre le legioni partiche durante l’impero di Settimio Severo e che era sotto il diretto comando dell’Imperatore. In questa prima sala troviamo alcuni importanti reperti archeologici quali l’Ara di Cassio Severiano, una prominente figura della Seconda Legione Partica, il centurione di più alto rango (Primipilus) dei Triarii della prima corte nonché ufficiale delle truppe responsabili dell’approvvigionamento e della sorveglianza dell’accampamento di Albano. Inoltre vi si possono ammirare anche un altorilievo marmoreo raffigurante i fasci littori e splendidi parastinchi da parata sui quali è riprodotta la figura del dio Marte. La seconda sala è dedicata ai Castra Albana con fotografie e testi che illustrano i monumenti facenti parte dell’accampamento ancora visibili nella città di Albano. Tra questi i Cisternoni, la più grande riserva d’acqua dell’accampamento, la Porta Pretoria, che costituiva l’ingresso principale dell’accampamento dalla via Appia, le Terme, fatte costruire da Caracalla per le sue truppe e l’Anfiteatro posto sulla sommità di una collina al di fuori dell’accampamento. Tutti fanno parte del circuito archeologico della città. In questa seconda sala è anche possibile ammirare alcuni resti architettonici quali la Testa turrita di Tyche-Fortuna, che dimostrano la ricchezza delle decorazioni dei Castra. Le due sale seguenti documentano la vita quotidiana dei legionari: utensili e altri oggetti da cucina come piatti in terra sigillata africana, anfore per il vino, l’olio o il “garum”(salsa piccante a base di pesce usata per condire i cibi), nonché lampade ad olio di forme differenti, dimostrano la circolazione di prodotti provenienti dalle provincie dell’impero, mentre i numerosi reperti monetari danno l’idea dei commerci che sicuramente si effettuavano intorno all’accampamento. Alcune punte di freccia e “ghiande missile” in piombo, usate come armi e altri oggetti particolari quali pedine e dadi oppure tessere di piombo che venivano usati come una sorta di biglietto d’ammissione all’anfiteatro forniscono ulteriori informazioni sulla vita nella Legione. Di manifattura locale erano probabilmente comuni terrecotte, altri oggetti di uso quotidiano nonché mattoni da costruzione e tegole utilizzate per la costruzione degli edifici, alcuni dei quali, rettangolari o in planta pedis, presentano l’incisione “LIIP” (Legio Secunda Parthica) oppure “LIIPS” (Legio Secunda Parthica Severiana). L’ultima sala è dedicata all’aspetto funerario come documentato dalla scoperta di ampie necropoli o singole tombe nella zona. Qui sono in mostra i coperchi con iscrizioni di alcuni sarcofagi di peperino “a baule”, il calco delle stele funerarie del ragazzo Euthyches e del principe della legione Eptecentus. Gli originali di queste ultime si trovano nella chiesa di Santa Maria della Rotonda. La sala contiene anche alcuni oggetti in terracotta quali piccoli vasi, brocche o lampade ad olio scoperti in una costruzione sepolcrale lungo la Via Appia. Infine, nella sala didattica i visitatori possono toccare e indossare fedeli riproduzioni di equipaggiamento militare della Legione e immergersi con la propria immaginazione nel vivo della battaglia. Attraverso un film documentario di archeologia sperimentale, i visitatori possono imparare molto sulla dura e disciplinata vita di un legionario romano, la figura centrale delle conquiste dell’impero nonché araldo della civiltà romana nel mondo.", "Viale Volontari del Sangue, 11/13", "lunedì:\t09:00-13:00; 15:30-18:30\nmartedì:\t09:00-13:00\nmercoledì:\t09:00-13:00\ngiovedì:\t09:00-13:00; 15:30-18:30\nvenerdì:\tchiuso\nsabato:\t09:00-13:00;\ndomenica:\t09:00 -13:00", "2 ore", "Singoli: 3 euro\nGruppi (max 30): 40 euro", "Musei", "museo_legione"));
        allBc.add(new BC("Palazzo Savelli", "Palazzo Savelli è un palazzo nobiliare di Albano Laziale, attualmente sede del Comune di Albano Laziale. Costruito nel Medioevo come fortezza lungo la via Appia, nel corso dei secoli si espanse occupando una vastissima superficie, inglobando i ruderi della Porta Pretoria e anche la Chiesa di San Pietro Apostolo, dietro alla quale era annessa la cappella di iuspatronatus della famiglia Savelli. Dopo l’acquisizione di Albano da parte della Camera Apostolica (1699) il palazzo divenne in parte sede del Governatorato e in parte residenza nobiliare concessa come residenza per la famiglia Stuart. Parte del palazzo venne abbattuta in seguito ai lavori di ampliamento della via Appia eseguiti tra il 1789 ed il 1791 ed il 1854. Nel 1870, dopo l’annessione del Lazio al Regno d’Italia, divenne sede del Comune di Albano, e fino a qualche anno fa anche della Pretura di Albano (che dagli anni ’30 accorpa anche quella di Marino). All’interno, sono da notare la grande scalinata che porta al piano nobile e la Sala Nobile, usata come aula consiliare, attualmente in ristrutturazione.", "Piazza Costituente, 1", "lunedì:\tND\nmartedì:\tND\nmercoledì:\tND\ngiovedì:\tND\nvenerdì:\tND\nsabato:\tND\ndomenica:\tND", "30 minuti", "Gratuito", "Storici", "palazzo_savelli"));
        allBc.add(new BC("Villa Doria - Pamphili", "Villa Doria-Pamphili, o semplicemente villa Doria, è un parco pubblico, anticamente villa nobiliare suburbana, della cittadina di Albano Laziale, in provincia di Roma. Edificata dal Cardinale Fabrizio Paolucci nel XVIII sec., fu acquistata in seguito dalla famiglia Doria, che fece edificare la palazzina su via Appia e curò la sistemazione del vasto parco. All’interno di Villa Doria si accampò un distaccamento della divisione di Fanteria “Piacenza”, dispersa poi dai tedeschi il 9 settembre 1943. La palazzina della villa, colpita dai bombardamenti alleati del 1º febbraio 1944, venne rasa al suolo nel 1951 dal comune di Albano. La villa diventò parco pubblico, uno dei più vasti dei Colli Albani. Al centro dell’area verde affiorano i resti di strutture romane, convenzionalmente attribuite ad una villa romana di proprietà di Pompeo Magno. Attualmente durante l’Estate viene utilizzata per gli spettacoli di “Albano Estate” e viene attivato da Giugno a Settembre un Bar con Piscina all’aperto e diverse attrezzature sportive.", "Piazza Mazzini", "lunedì:\t00:00-24:00\nmartedì:\t00:00-24:00\nmercoledì:\t00:00-24:00\ngiovedì:\t00:00-24:00\nvenerdì:\t00:00-24:00\nsabato:\t00:00-24:00\ndomenica:\t00:00-24:00", "1 ora", "Gratuito", "Parchi", "villa_doria"));
        allBc.add(new BC("Bajocco Festival", "L’Associazione Culturale XV Miglio è stata creata con un obiettivo preciso, la rivalutazione del territorio e delle imprese attraverso la creazione di eventi in grado di richiamare turisti e visitatori attratti dalle bellezze della Città, della sua storia millenaria, delle sue eccellenze eno-gastronomiche, delle sue tradizioni, dei sui miti e leggende. \nE’ proprio per questo che nel 2011 nasce il progetto Bajocco Festival degli artisti di strada. Si svolge nel centro storico di Albano Laziale. Per 3 giorni, solitamente nel secondo week end del mese di settembre, le esibizioni degli artisti occupano vie, vicoli e piazze dalle ore 21:00 alle ore 02:00.  La città si trasforma in un animato e colorato teatro a cielo aperto dove si incontrano acrobati, giocolieri, musicisti, clown, sapienti e abili manipolatori di gesti senza tempo. Musica, spettacoli circensi, strepitose evoluzioni acrobatiche, raffinate performance, suoni, colori e passioni mescolati dalla magia dell’arte di strada.\nIl nome “Bajocco” è stato scelto in riferimento ad una piccola ed antica moneta, utilizzata già dal 1500, ed adottato per sottolineare il gesto, comune tra gli artisti di strada, di esporre il proprio cappello a fine esibizione per raccogliere il frutto del loro lavoro e valutare il gradimento del pubblico. Questo gesto resta in uso ed è bene accettato anche se la maggior parte degli artisti viene regolarmente ingaggiata.\nIl Bajocco Festival Artisti di Strada ha ottenuto, alla seconda edizione, il Premio \"Meraviglia Italiana\", riconoscimento assegnato a “manifestazioni e siti di particolare, straordinario e alto valore culturale”. Il numero dei visitatori ed il gradimento del pubblico sono cresciuti negli anni in misura esponenziale, arrivando a sfiorare le 100.000 presenze nella V edizione (2015).\nIl festival è diviso in AREA CIRCENSE e AREA MUSICALE.  Le postazioni degli spettacoli variano in base alle caratteristiche dello spettacolo stesso. Le esibizioni si svolgono in contemporanea su diverse aree della città in base al programma di inizio e fine spettacoli.\nLa manifestazione col tempo si è caratterizzata per le proposte artistiche e culturali presenti in cartellone, nonché per lo spirito di condivisione che anima gli artisti e le maestranze invitate all'evento, principi questi che riteniamo siano parte fondamentale dell’evento.", "Tutta la città", "lunedì:\tND\nmartedì:\tND\nmercoledì:\tND\ngiovedì:\tND\nvenerdì:\tND\nsabato:\tND\ndomenica:\tND", "5 ore", "Gratuito", "Eventi culturali", "bajocco"));
        allBc.add(new BC("Albano Musica Festival", "L'Albano Musica Festival è nato nel 1991 con il bicentenario della morte di Mozart, per diffondere la musica da camera e sinfonica. Con le edizioni successive il festival si è aperto anche al teatro musicale, all'operetta, al tango e a concerti tematici sulla musica del '900.Alla ricchezza dei repertori musicali, spesso anche poco noti, si è affiancata la presenza di prestigiosi musicisti, orchestre e ensemble tra i quali ricordiamo: Simona Marchini, Bruno Canino, Fausto Di Cesare, Christian Pintilie, Franco Medori, Roberto Cappello, Aiman Mussakhojaeva, Dosto&Yevski, Sebastiano Maria Vianello, Astrid Wihlelmsen, Jacqueline Roscheck-Morard, Sabina Macculi, Gabriella Morigi, Hugo Aisenberg, I Solisti dell'Opera di Roma, Maurizio D'Alessandro, Michel Lethiec, Gaetano Russo, Andrea Noferini, Marco Angius, Quartetto Bernini, Nuova Orchestra Scarlatti, Quartetto Archimia, Compagnia Italiana di Operette, Caffè Concerto Strauss, Maria Grazia Schiavo, Orchestra Filarmonica di Oradea, Orchestra da Camera Benedetto Marcello, Orchestra Notte Sinfonica Veneziana, Quartetto Accademia, Orchestra Sinfonica Rossini di Pesaro, Ensemble Femminile Opern Ball di Vienna, Orchestra Filarmonica di Bacau, Musicomix Orchestra.", "Palazzo Savelli e Teatro Alba Radians", "lunedì:\tND\nmartedì:\tND\nmercoledì:\tND\ngiovedì:\tND\nvenerdì:\tND\nsabato:\tND\ndomenica:\tND", "ND", "ND", "Eventi culturali", "musica_festival"));
    }
    
    public static ArrayList<BC> getList()
    {
        if (bcList == null)
            bcList = new BCList();
        return allBc;
    }
    
    public static int getSize()
    {
        return allBc.size();
    }
    
    public class BC
    {
        private final String nome, descrizione, indirizzo, durata, tariffe, tipo, img, orariString;
        private final HashMap<String,String> orari = new HashMap<>();
        
        public BC(String nome, String descrizione, String indirizzo, String orari, String durata, String tariffe, String tipo, String img)
        {
            this.nome = nome;
            this.descrizione = descrizione;
            this.indirizzo = indirizzo;
            orariString = orari;
            String[] giorni = orari.split("\n");
            for (String giorno : giorni)
            {
                String[] orario = giorno.split("\t");
                this.orari.put(orario[0], orario[1]);
            }
            this.durata = durata;
            this.tariffe = tariffe;
            this.img = img;
            this.tipo = tipo;
        }
        
        public String getNome()
        {
            return nome;
        }
        
        public String getDescrizione()
        {
            return descrizione;
        }
        
        public HashMap<String, String> getOrari()
        {
            return orari;
        }
        
        public String getDurata()
        {
            return durata;
        }
        
        public String getTariffe()
        {
            return tariffe;
        }
        
        public String getTipo()
        {
            return tipo;
        }
        
        public String getImg()
        {
            return img;
        }
        
        public String getIndirizzo()
        {
            return indirizzo;
        }
    
        public String getOrariString()
        {
            return orariString;
        }
    }
}
